mapper.select:
    	SELECT id, title, content, helpful, score, mod_date AS modDate, reg_date AS regDate, user_id AS userId, company_id AS companyId
    	FROM  comment 
    	
   			 
					WHERE user_id=#{id} 
   			 
    		 
					WHERE company_id=#{id} 
    		
    		
  		
    	
    	ORDER BY id DESC 
    	
    	
    		limit #{start}, #{limit};

select count(*)
from comment
where company_id = #{company_id};
