mapper.select:
		SELECT id, name, age, gender, email, password, fail_password, cre_date AS creDate, last_date AS lastDate,
		total_reply AS totalReply, business_id AS businessId, image_info_id AS imageInfoId FROM user

		
			
				WHERE id = #{id}
			
			
				WHERE business_id = #{id}

SELECT id, name, age, gender, email, password, fail_password, cre_date AS creDate, last_date AS lastDate,
total_reply AS totalReply, business_id AS businessId, image_info_id AS imageInfoId FROM user
WHERE email = #{email}

mapper.update:
UPDATE user
name = #{name} , password = #{password} , gender = #{gender} , age = #{age} , image_info_id = #{imageInfoId} , business_id = #{businessId} ,
WHERE id=#{id}
WHERE email=#{email}

mapper.select:
	
		select email
		from user
		where id = #{id}
